mapper.select:
select count(1)
from test.date_test
where id=#{id}

select count(1)
from test.time_out
where id= #{id} and test_name like '?' and test_name = #{name}

select count(1)
from test.date_test
where id= #{id} and `time` = #{time}

select count(1)
from test.time_out
where `test_name` = #{testBean.name} and id = #{testBean.num}
